mapper.insert:

    SELECT IFNULL(CONCAT('cp',LPAD(MAX(SUBSTRING(company_id,3,4))+1,4,'0')),'cp0001') from md_company


insert into md_company
(
    company_id
    ,type
    ,name
    ,manager_name
    ,company_number
    ,mobile_phone_number
    ,company_phone_number
    ,address
    ,rm
    ,regist_id
    ,regist_date
    ,update_id
    ,update_date
)values(
    #{companyId}
    ,#{type}
    ,#{name}
    ,#{managerName}
    ,#{companyNumber}
    ,#{mobilePhoneNumber}
    ,#{companyPhoneNumber}
    ,#{address}
    ,#{rm}
    ,#{registId}
    ,now()
    ,#{updateId}
    ,now()
)
